package javaPrograms;

public class swapTwoNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int x =10; int y=20;
		
		System.out.println("Before Swap: " +"x= " + x + " y= " + y  );
		int temp=x;
		
		x=y;
		
	    y = temp;
	    
	    System.out.println("After Swap: " + "x= " + x + " y= " + y  );
		
		
		

	}

}
